import { useState } from "react";

const ToDoList = () => {

    const [checkbox, setCheckbox] = useState('')

    const addCheckbox = () => {
        const inputCheckbox = document.querySelector('form input[type="text"]');
        let checkSection = document.querySelector('#check');
        checkSection.innerHTML = `<input type="checkbox" name="inputCheckbox.value" id="" />`
        // setCheckbox(...checkbox, inputCheckbox.value)
        console.log(inputCheckbox.value);
    }

    return (
        <div>
            <form action="">
                <div id="check"></div>
                <input type="text" name="" id="" placeholder="Add ToDo" />
                <button type="submit" onClick={addCheckbox}>Submit</button>

            </form>
        </div>
    );
}

export default ToDoList;


