import { useState } from "react";

const ToDoList2 = () => {
    const [check, setCheck] = useState([]);

    const addCheck = () => {
        const inputText = document.querySelector('form input[type="text"]');
        // spread operator
        setCheck([...check, inputText.value]);
    }

    const [toggle, setToggle] = useState(true);
    const handleClick = () => {
        setToggle(!toggle)
    }

    return (
        <div>
            <form action="#">
                <input type="text" name="" id="" />
                <input type="button" value="Add ToDo" onClick={addCheck} />
            </form>
            <article>
                {check.map((elt) => {
                    return (
                        <form action="">
                            <input type="checkbox" name="" id="" />
                            <label>{elt} <button style={{ display: toggle ? 'block' : 'none' }}> l√∂schen</button></label>

                        </form>
                    )
                })}
            </article>
        </div>
    );
}

export default ToDoList2;
