import { useState } from "react";
import Item from "./Item";

const ToDoItem = () => {
    const [check, setCheck] = useState([]);

    const addCheck = () => {
        const inputText = document.querySelector('form input[type="text"]');
        // spread operator
        setCheck([...check, inputText.value]);
    }

    // const removeCheck = (item) => {
    //     console.log("i wanna be removed");
    //     const newCheck = check.filter((elt) => {
    //         elt.id !== id;
    //         setCheck(newCheck)
    //     });
    //     // setCheck((newState => newState.filter((newItem) => newItem !== item)))
    // }

    const removeCheck = value => {
        setCheck(oldValues => {
            return oldValues.filter(elt => elt !== value)
        })
    }

    return (
        <div>
            <form action="#">
                <input type="text" name="" id="" />
                <input type="button" value="Add ToDo" onClick={addCheck} />
            </form>
            <article>
                {check.map((elt) => {
                    return (
                        <div>
                            <Item task={elt} />

                        </div>
                    )
                })}
            </article>
        </div>
    );
}

export default ToDoItem;

