import { useState } from "react";

const Fruits = () => {
    const [fruits, setFruits] = useState([""]);

    const addFruits = () => {
        const inputFruits = document.querySelector('form input[type="text"]');
        // spread operator
        setFruits([...fruits, inputFruits.value]);
    }
    return (
        <div>
            <form action="#">
                <input type="text" name="" id="" />
                <input type="button" value="Add Fruits" onClick={addFruits} />
            </form>
            <article>
                {fruits.map((elt) => {
                    return (
                        <form action="">
                            <label>{elt}</label>
                            <input type="checkbox" name="" id="" />
                        </form>
                    )
                })}
            </article>
        </div>
    );
}

export default Fruits;
