import { useState, useEffect } from "react";
// import { useForm } from "react-hook-form"
import ToDoItem from "./ToDoItem";

const ToDoList = () => {
    const [todos, setTodos] = useState([]);

    useEffect(() => {
        const getTodos = async () => {
            const response = await fetch('http://localhost:3001/todos', {});
            const data = await response.json();
            setTodos(data);
        }
        getTodos();
    }, []);

    console.log(todos);


    // const addCheck = () => {
    //     const inputText = document.querySelector('form input[type="text"]');
    //     // spread operator - kopiert inhalt des arrays -nimm alles aus[todos und f端ge inputtext.value hinzu
    //     setTodos(...[todos, inputText.value]);
    //     document.querySelector('form input[type="text"]').value = "";
    // }

    const addCheck = async () => {
        const inputText = document.querySelector('form input[type="text"]');
        try {
            const response = await fetch("http://localhost:3001/todos", {
                method: "POST",
                body: JSON.stringify({ title: inputText, completed: false }),
                headers: {
                    "content-type": "application/json",
                }
            });
            const data = await response.json();
            setTodos(data);
            document.querySelector('form input[type="text"]').value = "";

            // const addCheck = () => {
            //     const inputText = document.querySelector('form input[type="text"]');
            //     // spread operator - kopiert inhalt des arrays -nimm alles aus[todos und f端ge inputtext.value hinzu
            //     // setTodos(...[todos, inputText.value]);
            //     document.querySelector('form input[type="text"]').value = "";
            // }
        }
        catch (error) {
            console.error(error)
        }
    }

    const removeCheck = value => {
        setTodos(oldValues => {
            return oldValues.filter(elt => elt !== value)
        })
    }

    return (
        <div>
            <form action="#">
                <input type="text" name="" id="" placeholder="Add ToDo" />
                <input type="submit" value="Add ToDo" onClick={addCheck} />
            </form>
            <article>
                {todos.map((elt) => {
                    return (
                        <div>
                            <ToDoItem task={elt.title} />
                            <button onClick={() => removeCheck(elt.id)}>delete</button>
                        </div>
                    )
                })}
            </article>
        </div >
    );
}

export default ToDoList;
