import { useState } from "react";

const ToDoItem = () => {
    const [check, setCheck] = useState([]);

    const addCheck = () => {
        const inputText = document.querySelector('form input[type="text"]');
        // spread operator
        setCheck([...check, inputText.value]);
    }

    // const [toggle, setToggle] = useState(true);
    // const handleClick = () => {
    //     setToggle(!toggle)
    // }

    const [showCheck, setShowCheck] = useState(true);

    return (
        <div>
            <form action="#">
                <input type="text" name="" id="" />
                <input type="button" value="Add ToDo" onClick={addCheck} />
            </form>
            <article>
                {check.map((elt) => {
                    return (
                        <form action="">
                            <input type="checkbox" name="" id="" />
                            {/* <label style={{ display: toggle ? 'block' : 'none' }}>{elt}</label> */}
                            <label>{elt}</label>
                            {/* <a onClick={handleClick}> delete</a> */}
                            <a onClick={event => setShowCheck(event.target.checked)}> delete</a>
                        </form>
                    )
                })}
            </article>
        </div>
    );
}

export default ToDoItem;
