import { useState, useEffect } from "react";
// import { useForm } from "react-hook-form"
import ToDoItem from "./ToDoItem";

const ToDoList = () => {
    const [todos, setTodos] = useState([]);



    const addCheck = () => {
        const inputText = document.querySelector('form input[type="text"]');
        // spread operator - kopiert inhalt des arrays -nimm alles aus[todos und f端ge inputtext.value hinzu
        setTodos(...[todos, inputText.value]);
        document.querySelector('form input[type="text"]').value = "";
    }

    // const onSubmit = async () => {
    //     try {
    //         await fetch("http://localhost:3001/todos", {
    //             method: "POST",
    //             body: JSON.stringify({ title }),
    //             headers: {
