    //             }
    //         });
    //         setTodos("");

    //         // const addCheck = () => {
    //         //     const inputText = document.querySelector('form input[type="text"]');
    //         //     // spread operator - kopiert inhalt des arrays -nimm alles aus[todos und f端ge inputtext.value hinzu
    //         //     setTodos([..[todos, inputText.value]);
    //         //     document.querySelector('form input[type="text"]').value = "";
    //         // }
    //     }
    //     catch (error) {
    //         console.error(error)
    //     }
    // }

    const removeCheck = value => {
        setTodos(oldValues => {
            return oldValues.filter(elt => elt !== value)
        })
    }

    return (
        <div>
            {/* <form action="#" onSubmit={handleSubmit(onSubmit)}> */}
            <form action="#">
                <input type="text" name="" id="" placeholder="Add ToDo" />
                <input type="button" value="Add ToDo" onClick={addCheck} />
                {/* <input type="submit" value="add ToDo" /> */}
            </form>
            <article>
                {todos.map((elt) => {
                    return (
                        <div>
                            <ToDoItem task={elt} />
                            <button onClick={() => removeCheck(elt)}>delete</button>
                        </div>
                    )
                })}
            </article>
        </div >
    );
}

export default ToDoList;
